examples.example:
yaml
x-pubsubdb-maps:
 temperature_description:
 "@pipe":
 - ["{a.output.data.temperature}", 20]
 - ["{@compare.lte}"]
 - ["cold", "warm"]
 - ["{@conditional.ternary}"]


Object A:
json
{
 "output": {
 "data": {
 "temperature": 15
 }
 }
}


Resulting JSON object:
json
{
 "temperature_description": "cold"
}

yaml
x-pubsubdb-maps:
 priority:
 "@pipe":
 - ["{a.output.data.status}", "urgent"]
 - ["{@string.equals}"]
 - ["high", "normal"]
 - ["{@conditional.ternary}"]


Object A:
json
{
 "output": {
 "data": {
 "status": "urgent"
 }
 }
}


Resulting JSON object:
json
{
 "priority": "high"
}
